import React, { useState } from 'react'
import './Square.css'
import Board from './Board';
function Square() {
    const [temp,setTemp] = useState("X");
    const [value,setValue] = useState("O");
    const changeValue = () =>{
        setValue(temp);
        setTemp(value);
    } 
    return (
    <button className='square' onClick={()=>changeValue()}>
        {value}
    </button>
  )
}

export default Square
